import { ThemeProvider } from "@emotion/react";
import { Copyright } from "@mui/icons-material";
import ListAltIcon from '@mui/icons-material/ListAlt';
import { CssBaseline, AppBar, Toolbar, Typography, Box, Container, Stack, Button, Grid, Card, CardMedia, CardContent, CardActions, createTheme, makeStyles, TextField } from "@mui/material";
import React, { FC, useState } from "react";
import { RouteComponentProps } from "react-router-dom";
import {useQuery} from 'react-query'
import axios from 'axios'
import AddIcon from '@mui/icons-material/Add';


const theme = createTheme();


type TasksComponentProps = RouteComponentProps;
const Tasks: FC<TasksComponentProps> = ({ history }) => {

  const config = {
      headers: { Authorization: `Bearer ${localStorage.getItem("auth")}` }
  };

  const logout = () => {
    localStorage.clear();
    history.push("/login");
  };

  async function fetchTasks(){
    const {data} = await axios.get(`https://localhost:7008/api/Task/TaskList?id=${localStorage.getItem("id")}`, config)    
    return data
  }
    
  const {data, error, isError, isLoading } = useQuery('posts', fetchTasks) 

  if(isLoading){
      return <div>Loading...</div>
  }
  if(isError){
      return <div>Error!</div>
  }

  
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppBar position="static">
        <Toolbar>
        <ListAltIcon sx={{ mr: 2 }} />
        <Typography variant="h6" color="inherit" noWrap  sx={{ flexGrow: 1 }}>
            To Do List
          </Typography>
          <Button color="inherit" onClick={logout}>Log Out</Button>
        </Toolbar>
      </AppBar>
      <main>
        <Box
          sx={{
            bgcolor: 'background.paper',
            pt: 8,
            pb: 6,
          }}
        >
          <Container maxWidth="sm">
            <Typography
              component="h1"
              variant="h2"
              align="center"
              color="text.primary"
              gutterBottom
            >
              Hello {localStorage.getItem("username")}ðŸ‘‹
            </Typography>
            <Typography variant="h5" align="center" color="text.secondary" paragraph>
              You currently have {data.length} active tasks.
            </Typography>
            <Stack
              sx={{ pt: 4 }}
              direction="row"
              spacing={2}
              justifyContent="center"
            >
              <Grid container spacing={2}>
                <Grid item xs={9}>
                <TextField
                  margin="normal"
                  variant="filled"
                  fullWidth
                  required
                  id="username"
                  label="Username"
                  autoComplete="username"
                  autoFocus
                />
                </Grid>
                <Grid item xs={3}>
                  <Button style={{width:"100%", height:"100%"}} variant="contained" startIcon={<AddIcon />}>
                  Add
                </Button>
                </Grid>
              </Grid>
              
             
            </Stack>
          </Container>
        </Box>
        <Container sx={{ py: 8 }} maxWidth="md">

        <Grid container spacing={4}>
        </Grid>
        </Container>
      </main>
    </ThemeProvider>
  );
}

export default Tasks;
