import { ThemeProvider } from "@emotion/react";
import { Copyright } from "@mui/icons-material";
import ListAltIcon from '@mui/icons-material/ListAlt';
import { CssBaseline, AppBar, Toolbar, Typography, Box, Container, Stack, Button, Grid, Card, CardMedia, CardContent, CardActions, createTheme } from "@mui/material";
import React, { FC } from "react";
import { RouteComponentProps } from "react-router-dom";
import {useQuery} from 'react-query'
import axios from 'axios'


const theme = createTheme();




type SomeComponentProps = RouteComponentProps;
const Tasks: FC<SomeComponentProps> = ({ history }) => {
  const logout = () => {
    localStorage.clear();
    history.push("/login");
  };

  async function fetchTasks(){
    let param = {
      id: localStorage.getItem("id")
    };
    const {data} = await axios.post('https://localhost:7008/api/Task/TaskList', param)    
    return data
  }
    
    const {data, error, isError, isLoading } = useQuery('posts', fetchTasks) 
    if(isLoading){
        return <div>Loading...</div>
    }
    if(isError){
        return <div>Error!</div>
    }
  
  
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppBar position="static">
        <Toolbar>
        <ListAltIcon sx={{ mr: 2 }} />
        <Typography variant="h6" color="inherit" noWrap  sx={{ flexGrow: 1 }}>
            To Do List
          </Typography>
          <Button color="inherit" onClick={logout}>Log Out</Button>
        </Toolbar>
      </AppBar>
      <main>
        {/* Hero unit */}
        <Box
          sx={{
            bgcolor: 'background.paper',
            pt: 8,
            pb: 6,
          }}
        >
          <Container maxWidth="sm">
            <Typography
              component="h1"
              variant="h2"
              align="center"
              color="text.primary"
              gutterBottom
            >
              Hello {localStorage.getItem("username")}ðŸ‘‹
            </Typography>
            <Typography variant="h5" align="center" color="text.secondary" paragraph>
              Something short and leading about the collection belowâ€”its contents,
              the creator, etc. Make it short and sweet, but not too short so folks
              don&apos;t simply skip over it entirely.
            </Typography>
            <Stack
              sx={{ pt: 4 }}
              direction="row"
              spacing={2}
              justifyContent="center"
            >
              <Button variant="contained">Main call to action</Button>
              <Button variant="outlined">Secondary action</Button>
            </Stack>
          </Container>
        </Box>
        <Container sx={{ py: 8 }} maxWidth="md">
          {/* End hero unit */}
          <Grid container spacing={4}>
            {/* {cards.map((card) => (
              <Grid item key={card} xs={12} sm={6} md={4}>
                <Card
                  sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                >
                  <CardMedia
                    component="img"
                    sx={{
                      // 16:9
                      pt: '56.25%',
                    }}
                    image="https://source.unsplash.com/random"
                    alt="random"
                  />
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography gutterBottom variant="h5" component="h2">
                      Heading
                    </Typography>
                    <Typography>
                      This is a media card. You can use this section to describe the
                      content.
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button size="small">View</Button>
                    <Button size="small">Edit</Button>
                  </CardActions>
                </Card>
              </Grid>
            ))} */}
          </Grid>
        </Container>
      </main>
      {/* Footer */}
      <Box sx={{ bgcolor: 'background.paper', p: 6 }} component="footer">
        <Typography variant="h6" align="center" gutterBottom>
          Footer
        </Typography>
        <Typography
          variant="subtitle1"
          align="center"
          color="text.secondary"
          component="p"
        >
          Something here to give the footer a purpose!
        </Typography>
      </Box>
      {/* End footer */}
    </ThemeProvider>
  );
}

export default Tasks;
